mapper.select:
select * from (
	select rownum as rn, a.* from (
		select * from member

			where member_id = #{memberDto.member_id}
			where member_name = #{memberDto.member_name}
			where member_role = #{memberDto.member_role}

		order by member_idx desc
	)a
)
where rn between 1 + #{pagingDto.countPerPage} * (#{pagingDto.page} - 1) and #{pagingDto.countPerPage} * #{pagingDto.page}

select count(*) from member 

	where member_id = #{member_id}
	where member_name = #{member_name}
	where member_role = #{member_role}
